import 'package:flutter/material.dart';

class CategoryChip extends StatelessWidget {
  final String name;
  final bool isSelected;
  final VoidCallback onTap;

  const CategoryChip({
    super.key,
    required this.name,
    this.isSelected = false,
    required this.onTap,
  });

  @override
  Widget build(BuildContext context) {
    final primaryColor = Theme.of(context).primaryColor;
    final textTheme = Theme.of(context).textTheme;

    // Use OutlinedButton.styleFrom for consistency with the project's theme
    final ButtonStyle style = Theme.of(context).outlinedButtonTheme.style!.copyWith(
          backgroundColor: MaterialStateProperty.resolveWith<Color>(
            (Set<MaterialState> states) {
              if (isSelected) {
                return primaryColor;
              }
              return Colors.white; // White background for unselected
            },
          ),
          foregroundColor: MaterialStateProperty.resolveWith<Color>(
            (Set<MaterialState> states) {
              if (isSelected) {
                return Colors.white;
              }
              return Colors.black; // Black text for unselected
            },
          ),
          side: MaterialStateProperty.resolveWith<BorderSide>(
            (Set<MaterialState> states) {
              if (isSelected) {
                return BorderSide(color: primaryColor, width: 1.5);
              }
              return BorderSide(color: Colors.grey[300]!, width: 1.5); // Light grey border for unselected
            },
          ),
          shape: MaterialStateProperty.all<RoundedRectangleBorder>(
            RoundedRectangleBorder(borderRadius: BorderRadius.circular(25.0)),
          ),
          padding: MaterialStateProperty.all<EdgeInsetsGeometry>(
            const EdgeInsets.symmetric(horizontal: 16.0, vertical: 10.0),
          ),
        );

    return OutlinedButton(
      onPressed: onTap,
      style: style,
      child: Text(
        name,
        style: textTheme.bodyLarge?.copyWith(
          color: isSelected ? Colors.white : Colors.black,
          fontWeight: isSelected ? FontWeight.bold : FontWeight.normal,
        ),
      ),
    );
  }
}
