      ScaffoldMessenger.of(context).showSnackBar(
        SnackBar(
          content: Text('Interests selected: ${selectedInterests.join(', ')}'),
          duration: const Duration(seconds: 3),
        ),
      );
      // Navigate to the next screen (e.g., home screen)
    }
  }

  @override
